import { useDeviceInfo } from '@/hooks/useDeviceInfo'
import {
    Button,
    Card,
    CheckList,
    Divider,
    Form,
    Input,
    Space,
    Toast
} from 'antd-mobile'
import {
    EyeInvisibleOutline,
    EyeOutline,
    LockOutline,
    UserOutline
} from 'antd-mobile-icons'
import { useState } from 'react'
import './Login.css'

export default function Login() {
    const { isMobile, isTablet } = useDeviceInfo()

    const [form] = Form.useForm()
    const [loading, setLoading] = useState(false)
    const [showPassword, setShowPassword] = useState(false)
    const [rememberMe, setRememberMe] = useState(false)

    const handleLogin = async (values: any) => {
        setLoading(true)
        try {
            // TODO: 
            console.log('Login values:', values)
            await new Promise(resolve => setTimeout(resolve, 1000)) // API

            Toast.show({
                icon: 'success',
                content: '',
            })

            history.push('/')
        } catch (error) {
            Toast.show({
                icon: 'fail',
                content: '',
            })
        } finally {
            setLoading(false)
        }
    }

    const handleForgotPassword = () => {
        Toast.show('')
    }

    const handleRegister = () => {
        Toast.show('')
    }

    const handleQuickLogin = (type: string) => {
        Toast.show(`${type}`)
    }

    return (
        <div className={`login-page ${isMobile ? 'mobile' : ''} ${isTablet ? 'tablet' : ''}`}>
            <div className="login-container">
                {/* Logo */}
                <div className="login-header">
                    <div className="logo">
                        <div className="logo-icon"></div>
                        <h1 className="logo-text"></h1>
                    </div>
                    <p className="login-subtitle"></p>
                </div>

                {/*  */}
                <Card className="login-card">
                    <Form
                        form={form}
                        onFinish={handleLogin}
                        layout="vertical"
                        footer={
                            <Space direction="vertical" block>
                                <Button
                                    block
                                    type="submit"
                                    color="primary"
                                    size="large"
                                    loading={loading}
                                >

                                </Button>

                                <div className="login-options">
                                    <CheckList
                                        value={rememberMe ? ['remember'] : []}
                                        onChange={(val) => setRememberMe(val.includes('remember'))}
                                    >
                                        <CheckList.Item value="remember"></CheckList.Item>
                                    </CheckList>
                                    <Button
                                        fill="none"
                                        size="small"
                                        onClick={handleForgotPassword}
                                    >

                                    </Button>
                                </div>
                            </Space>
                        }
                    >
                        <Form.Item
                            name="username"
                            label=""
                            rules={[
                                { required: true, message: '' },
                                { min: 3, message: '3' }
                            ]}
                        >
                            <Input
                                placeholder=""
                                prefix={<UserOutline />}
                                clearable
                            />
                        </Form.Item>

                        <Form.Item
                            name="password"
                            label=""
                            rules={[
                                { required: true, message: '' },
                                { min: 6, message: '6' }
                            ]}
                        >
                            <Input
                                placeholder=""
                                type={showPassword ? 'text' : 'password'}
                                prefix={<LockOutline />}
                                suffix={
                                    <div onClick={() => setShowPassword(!showPassword)}>
                                        {showPassword ? <EyeOutline /> : <EyeInvisibleOutline />}
                                    </div>
                                }
                                clearable
                            />
                        </Form.Item>
                    </Form>
                </Card>

                {/*  */}
                <Divider className="login-divider"></Divider>

                {/*  */}
                <Card className="quick-login-card">
                    <div className="quick-login-title"></div>
                    <Space direction="vertical" block>
                        <Button
                            block
                            color="success"
                            size="large"
                            onClick={() => handleQuickLogin('')}
                        >

                        </Button>
                        <Button
                            block
                            size="large"
                            onClick={() => handleQuickLogin('')}
                        >

                        </Button>
                    </Space>
                </Card>

                {/*  */}
                <div className="register-link">
                    <span></span>
                    <Button
                        fill="none"
                        color="primary"
                        onClick={handleRegister}
                    >

                    </Button>
                </div>

                {/*  */}
                <div className="login-footer">
                    <p></p>
                </div>
            </div>
        </div>
    )
}

