import { apiClient } from '@/api/client'
import { Button, Form, Input, NavBar, Space, Steps, Toast } from 'antd-mobile'
import React, { useState } from 'react'
import { history } from 'umi'
import './Auth.css'

interface ForgotPasswordForm {
    email: string
    verificationCode: string
    newPassword: string
    confirmPassword: string
}

const ForgotPassword: React.FC = () => {
    const [form] = Form.useForm<ForgotPasswordForm>()
    const [loading, setLoading] = useState(false)
    const [currentStep, setCurrentStep] = useState(0)
    const [countdown, setCountdown] = useState(0)
    const [email, setEmail] = useState('')

    // 
    const sendVerificationCode = async () => {
        try {
            const emailValue = form.getFieldValue('email')
            if (!emailValue) {
                Toast.show({ content: '', position: 'top' })
                return
            }

            setLoading(true)
            await apiClient.post('/auth/send-reset-code', { email: emailValue })

            setEmail(emailValue)
            setCountdown(60)
            const timer = setInterval(() => {
                setCountdown((prev) => {
                    if (prev <= 1) {
                        clearInterval(timer)
                        return 0
                    }
                    return prev - 1
                })
            }, 1000)

            Toast.show({ content: '', position: 'top' })
            setCurrentStep(1)
        } catch (error) {
            console.error('Send verification code failed:', error)
            Toast.show({ content: '', position: 'top' })
        } finally {
            setLoading(false)
        }
    }

    // 
    const verifyCode = async () => {
        try {
            const values = form.getFieldsValue(['verificationCode'])
            if (!values.verificationCode) {
                Toast.show({ content: '', position: 'top' })
                return
            }

            setLoading(true)
            await apiClient.post('/auth/verify-reset-code', {
                email,
                code: values.verificationCode,
            })

            Toast.show({ content: '', position: 'top' })
            setCurrentStep(2)
        } catch (error) {
            console.error('Verify code failed:', error)
            Toast.show({ content: '', position: 'top' })
        } finally {
            setLoading(false)
        }
    }

    // 
    const resetPassword = async () => {
        try {
            const values = form.getFieldsValue(['newPassword', 'confirmPassword'])

            if (values.newPassword !== values.confirmPassword) {
                Toast.show({ content: '', position: 'top' })
                return
            }

            setLoading(true)
            await apiClient.post('/auth/reset-password', {
                email,
                code: form.getFieldValue('verificationCode'),
                newPassword: values.newPassword,
            })

            Toast.show({ content: '', position: 'top' })

            setTimeout(() => {
                history.push('/login')
            }, 1000)
        } catch (error) {
            console.error('Reset password failed:', error)
            Toast.show({ content: '', position: 'top' })
        } finally {
            setLoading(false)
        }
    }

    const handleNext = () => {
        if (currentStep === 0) {
            sendVerificationCode()
        } else if (currentStep === 1) {
            verifyCode()
        } else if (currentStep === 2) {
            resetPassword()
        }
    }

    const steps = [
        { title: '', description: '' },
        { title: '', description: '' },
        { title: '', description: '' },
    ]

    return (
        <div className="auth-container">
            <NavBar onBack={() => history.back()}></NavBar>

            <div className="auth-content">
                <div className="auth-header">
                    <h1 className="auth-title"></h1>
                    <p className="auth-subtitle"></p>
                </div>

                <div className="forgot-password-steps">
                    <Steps current={currentStep} direction="horizontal">
                        {steps.map((step, index) => (
                            <Steps.Step
                                key={index}
                                title={step.title}
                                description={step.description}
                            />
                        ))}
                    </Steps>
                </div>

                <Form form={form} layout="vertical" className="auth-form">
                    {currentStep === 0 && (
                        <Form.Item
                            name="email"
                            label=""
                            rules={[
                                { required: true, message: '' },
                                { type: 'email', message: '' },
                            ]}
                        >
                            <Input placeholder="" />
                        </Form.Item>
                    )}

                    {currentStep === 1 && (
                        <Space direction="vertical" style={{ width: '100%' }}>
                            <div className="verification-info">
                                <p>{email}</p>
                                {countdown > 0 && <p className="countdown"> ({countdown}s)</p>}
                            </div>
                            <Form.Item
                                name="verificationCode"
                                label=""
                                rules={[
                                    { required: true, message: '' },
                                    { len: 6, message: '6' },
                                ]}
                            >
                                <Input placeholder="6" maxLength={6} />
                            </Form.Item>
                            {countdown === 0 && (
                                <Button
                                    fill="none"
                                    color="primary"
                                    onClick={sendVerificationCode}
                                    loading={loading}
                                >

                                </Button>
                            )}
                        </Space>
                    )}

                    {currentStep === 2 && (
                        <Space direction="vertical" style={{ width: '100%' }}>
                            <Form.Item
                                name="newPassword"
                                label=""
                                rules={[
                                    { required: true, message: '' },
                                    { min: 6, message: '6' },
                                    { max: 20, message: '20' },
                                ]}
                            >
                                <Input placeholder="" type="password" />
                            </Form.Item>

                            <Form.Item
                                name="confirmPassword"
                                label=""
                                rules={[
                                    { required: true, message: '' },
                                    ({ getFieldValue }) => ({
                                        validator(_, value) {
                                            if (!value || getFieldValue('newPassword') === value) {
                                                return Promise.resolve()
                                            }
                                            return Promise.reject(new Error(''))
                                        },
                                    }),
                                ]}
                            >
                                <Input placeholder="" type="password" />
                            </Form.Item>
                        </Space>
                    )}

                    <div className="auth-actions">
                        <Button
                            color="primary"
                            size="large"
                            loading={loading}
                            onClick={handleNext}
                            block
                        >
                            {currentStep === 0 && ''}
                            {currentStep === 1 && ''}
                            {currentStep === 2 && ''}
                        </Button>
                    </div>
                </Form>

                <div className="auth-footer">
                    <Button
                        fill="none"
                        color="primary"
                        onClick={() => history.push('/login')}
                        className="auth-link"
                    >

                    </Button>
                </div>
            </div>
        </div>
    )
}

export default ForgotPassword

