import NetworkStatusMonitor from '@/components/NetworkStatusMonitor';
import '@/styles/antd-mobile.css';
import { nativeBridge } from '@/utils/nativeBridge';
import { networkManager } from '@/utils/networkManager';
import { networkTester, type NetworkTestResult } from '@/utils/networkTester';
import {
    Badge,
    Button,
    Card,
    Collapse,
    Dialog,
    Empty,
    List,
    NavBar,
    NoticeBar,
    Popup,
    Space,
    SpinLoading,
    SwipeAction,
    SwiperRef,
    Tabs,
    Tag,
    Toast
} from 'antd-mobile';
import {
    CheckCircleOutline,
    ClockCircleOutline,
    CloseCircleOutline,
    DeleteOutline,
    DownlandOutline,
    ExclamationCircleOutline,
    FileOutline,
    RedoOutline,
    RightOutline,
    SetOutline,
    StarOutline,
    UploadOutline
} from 'antd-mobile-icons';
import React, { useEffect, useRef, useState } from 'react';

const NetworkTest: React.FC = () => {
    const [testResult, setTestResult] = useState<NetworkTestResult | null>(null);
    const [isRunning, setIsRunning] = useState(false);
    const [testHistory, setTestHistory] = useState<NetworkTestResult[]>([]);
    const [cacheStats, setCacheStats] = useState<{ size: number; keys: string[] }>({ size: 0, keys: [] });
    const [showReport, setShowReport] = useState(false);
    const [activeTab, setActiveTab] = useState('status');
    const [showQuickActions, setShowQuickActions] = useState(false);
    const swiperRef = useRef<SwiperRef>(null);

    useEffect(() => {
        // 
        setTestHistory(networkTester.getTestHistory());

        // 
        updateCacheStats();

        // 
        const interval = setInterval(updateCacheStats, 5000);

        return () => clearInterval(interval);
    }, []);

    const updateCacheStats = () => {
        setCacheStats(networkManager.getCacheStats());
    };

    const runNetworkTest = async () => {
        if (isRunning) return;

        setIsRunning(true);
        Toast.show({
            icon: <SpinLoading />,
            content: '...',
            duration: 0
        });

        try {
            const result = await networkTester.runFullTest();
            setTestResult(result);
            setTestHistory(networkTester.getTestHistory());

            Toast.show({
                icon: result.nativeBridgeWorking ? <CheckCircleOutline /> : <CloseCircleOutline />,
                content: result.nativeBridgeWorking ? '' : '',
            });
        } catch (error) {
            console.error(':', error);
            Toast.show({
                icon: <CloseCircleOutline />,
                content: '',
            });
        } finally {
            setIsRunning(false);
        }
    };

    const testOfflineFunctionality = async () => {
        Toast.show({
            icon: <SpinLoading />,
            content: '...',
        });

        try {
            const success = await networkTester.testOfflineFunctionality();
            if (success) {
                Toast.show({
                    icon: <CheckCircleOutline />,
                    content: '',
                });
            } else {
                Toast.show({
                    icon: <CloseCircleOutline />,
                    content: '',
                });
            }
            updateCacheStats();
        } catch (error) {
            Toast.show({
                icon: <CloseCircleOutline />,
                content: `: ${error}`,
            });
        }
    };

    const simulateWeakNetwork = async () => {
        Toast.show({
            icon: <SpinLoading />,
            content: '...',
        });

        try {
            await networkTester.simulateWeakNetwork();
            Toast.show({
                icon: <CheckCircleOutline />,
                content: '',
            });
        } catch (error) {
            Toast.show({
                icon: <CloseCircleOutline />,
                content: `: ${error}`,
            });
        }
    };

    const clearCache = async () => {
        const result = await Dialog.confirm({
            content: '',
            confirmText: '',
            cancelText: '',
        });

        if (result) {
            Toast.show({
                icon: <SpinLoading />,
                content: '...',
            });

            try {
                await networkManager.clearCache();
                updateCacheStats();
                Toast.show({
                    icon: <CheckCircleOutline />,
                    content: '',
                });
            } catch (error) {
                Toast.show({
                    icon: <CloseCircleOutline />,
                    content: `: ${error}`,
                });
            }
        }
    };

    const clearTestHistory = () => {
        Dialog.confirm({
            content: '',
            confirmText: '',
            cancelText: '',
        }).then((result) => {
            if (result) {
                networkTester.clearTestHistory();
                setTestHistory([]);
                setTestResult(null);
                Toast.show({
                    icon: <CheckCircleOutline />,
                    content: '',
                });
            }
        });
    };

    const generateReport = () => {
        const report = networkTester.generateTestReport();
        console.log(':', report);

        // 
        if (navigator.clipboard) {
            navigator.clipboard.writeText(report).then(() => {
                Toast.show({
                    icon: <CheckCircleOutline />,
                    content: '',
                });
            });
        } else {
            setShowReport(true);
        }
    };

    const testNativeBridge = async () => {
        Toast.show({
            icon: <SpinLoading />,
            content: '...',
        });

        try {
            // 
            const deviceInfo = await nativeBridge.getDeviceInfo();
            console.log(':', deviceInfo);

            // 
            const networkInfo = await nativeBridge.getNetworkStatus();
            console.log(':', networkInfo);

            // Toast
            await nativeBridge.showToast('', 'short');

            Toast.show({
                icon: <CheckCircleOutline />,
                content: '',
            });
        } catch (error) {
            Toast.show({
                icon: <CloseCircleOutline />,
                content: `: ${error}`,
            });
        }
    };

    const getStatusIcon = (isOnline: boolean, quality: string) => {
        if (!isOnline) {
            return <CloseCircleOutline className="text-red-500" />;
        }

        switch (quality) {
            case 'excellent':
                return <CheckCircleOutline className="text-green-500" />;
            case 'good':
                return <CheckCircleOutline className="text-blue-500" />;
            case 'poor':
                return <ExclamationCircleOutline className="text-yellow-500" />;
            default:
                return <CloseCircleOutline className="text-red-500" />;
        }
    };

    const getStatusColor = (isOnline: boolean, quality: string) => {
        if (!isOnline) return 'danger';

        switch (quality) {
            case 'excellent': return 'success';
            case 'good': return 'primary';
            case 'poor': return 'warning';
            default: return 'danger';
        }
    };

    const getStatusText = (isOnline: boolean, quality: string) => {
        if (!isOnline) return '';

        switch (quality) {
            case 'excellent': return '';
            case 'good': return '';
            case 'poor': return '';
            default: return '';
        }
    };

    const renderNetworkStatus = () => (
        <div className="space-y-4">
            {/*  */}
            <Card>
                <div className="p-4">
                    <div className="flex items-center justify-between mb-4">
                        <span className="text-lg font-semibold"></span>
                        <NetworkStatusMonitor showDetails={false} />
                    </div>

                    {testResult && (
                        <div className="space-y-4">
                            {/*  */}
                            <div className="grid grid-cols-2 gap-3">
                                <div className="text-center p-4 bg-gradient-to-br from-blue-50 to-blue-100 rounded-lg">
                                    <div className="text-2xl font-bold text-blue-600 mb-1">
                                        {testResult.isOnline ? '' : ''}
                                    </div>
                                    <div className="text-sm text-blue-600"></div>
                                </div>

                                <div className="text-center p-4 bg-gradient-to-br from-green-50 to-green-100 rounded-lg">
                                    <div className="text-2xl font-bold text-green-600 mb-1">
                                        {testResult.signalStrength}%
                                    </div>
                                    <div className="text-sm text-green-600"></div>
                                </div>
                            </div>

                            {/*  */}
                            <div className="space-y-2">
                                <div className="flex items-center justify-between text-sm">
                                    <span></span>
                                    <span className="font-medium">{testResult.networkQuality}</span>
                                </div>
                                <div className="w-full bg-gray-200 rounded-full h-2">
                                    <div
                                        className={`h-2 rounded-full transition-all duration-300 ${testResult.isOnline ?
                                            (testResult.networkQuality === 'excellent' ? 'bg-green-500' :
                                                testResult.networkQuality === 'good' ? 'bg-blue-500' : 'bg-yellow-500') : 'bg-red-500'
                                            }`}
                                        style={{ width: `${testResult.signalStrength}%` }}
                                    />
                                </div>
                            </div>

                            {/*  */}
                            {testResult.pingTime && (
                                <div className="grid grid-cols-3 gap-2">
                                    <div className="text-center p-3 bg-blue-50 rounded-lg">
                                        <div className="flex items-center justify-center mb-1">
                                            <ClockCircleOutline className="text-blue-500 mr-1" />
                                            <span className="text-sm font-semibold text-blue-600">
                                                {testResult.pingTime}ms
                                            </span>
                                        </div>
                                        <div className="text-xs text-blue-600">Ping</div>
                                    </div>

                                    {testResult.downloadSpeed && (
                                        <div className="text-center p-3 bg-green-50 rounded-lg">
                                            <div className="flex items-center justify-center mb-1">
                                                <DownlandOutline className="text-green-500 mr-1" />
                                                <span className="text-sm font-semibold text-green-600">
                                                    {testResult.downloadSpeed}KB/s
                                                </span>
                                            </div>
                                            <div className="text-xs text-green-600"></div>
                                        </div>
                                    )}

                                    {testResult.uploadSpeed && (
                                        <div className="text-center p-3 bg-purple-50 rounded-lg">
                                            <div className="flex items-center justify-center mb-1">
                                                <UploadOutline className="text-purple-500 mr-1" />
                                                <span className="text-sm font-semibold text-purple-600">
                                                    {testResult.uploadSpeed}KB/s
                                                </span>
                                            </div>
                                            <div className="text-xs text-purple-600"></div>
                                        </div>
                                    )}
                                </div>
                            )}

                            {/*  */}
                            <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                                <div className="flex items-center">
                                    <SetOutline className="text-gray-500 mr-2" />
                                    <span className="text-sm"></span>
                                </div>
                                <Tag color={testResult.nativeBridgeWorking ? 'success' : 'danger'}>
                                    {testResult.nativeBridgeWorking ? '' : ''}
                                </Tag>
                            </div>

                            {/*  */}
                            {testResult.errors.length > 0 && (
                                <div className="p-3 bg-red-50 rounded-lg">
                                    <div className="flex items-center mb-2">
                                        <ExclamationCircleOutline className="text-red-500 mr-2" />
                                        <span className="text-sm font-semibold text-red-800"></span>
                                    </div>
                                    <div className="text-xs text-red-700 space-y-1">
                                        {testResult.errors.map((error, index) => (
                                            <div key={index}> {error}</div>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>
                    )}
                </div>
            </Card>

            {/*  */}
            <Card>
                <div className="p-4">
                    <div className="flex items-center justify-between mb-4">
                        <span className="text-lg font-semibold"></span>
                        <Button
                            size="small"
                            fill="outline"
                            onClick={() => setShowQuickActions(!showQuickActions)}
                        >
                            {showQuickActions ? '' : ''}
                        </Button>
                    </div>

                    {showQuickActions && (
                        <div className="space-y-3">
                            <Button
                                block
                                color="primary"
                                loading={isRunning}
                                onClick={runNetworkTest}
                                disabled={isRunning}
                            >
                                <RedoOutline className="mr-2" />
                                {isRunning ? '...' : ''}
                            </Button>

                            <div className="grid grid-cols-2 gap-2">
                                <Button
                                    color="success"
                                    onClick={testNativeBridge}
                                >
                                    <SetOutline className="mr-2" />

                                </Button>
                                <Button
                                    color="warning"
                                    onClick={testOfflineFunctionality}
                                >
                                    <FileOutline className="mr-2" />

                                </Button>
                            </div>
                        </div>
                    )}
                </div>
            </Card>
        </div>
    );

    const renderTestControls = () => (
        <div className="space-y-4">
            {/*  */}
            <Card>
                <div className="p-4">
                    <div className="mb-4">
                        <span className="text-lg font-semibold"></span>
                    </div>

                    <div className="space-y-3">
                        <Button
                            block
                            color="primary"
                            loading={isRunning}
                            onClick={runNetworkTest}
                            disabled={isRunning}
                        >
                            <RedoOutline className="mr-2" />
                            {isRunning ? '...' : ''}
                        </Button>

                        <div className="grid grid-cols-2 gap-3">
                            <Button
                                color="success"
                                onClick={testNativeBridge}
                            >
                                <SetOutline className="mr-2" />

                            </Button>

                            <Button
                                color="warning"
                                onClick={testOfflineFunctionality}
                            >
                                <FileOutline className="mr-2" />

                            </Button>
                        </div>

                        <Button
                            block
                            color="default"
                            onClick={simulateWeakNetwork}
                        >
                            <ExclamationCircleOutline className="mr-2" />

                        </Button>
                    </div>
                </div>
            </Card>

            {/*  */}
            <Card>
                <div className="p-4">
                    <div className="mb-4">
                        <span className="text-lg font-semibold"></span>
                    </div>

                    <Collapse>
                        <Collapse.Panel key="1" title="">
                            <div className="space-y-3">
                                <Button
                                    size="small"
                                    fill="outline"
                                    color="primary"
                                    onClick={() => {
                                        Toast.show({ content: 'Ping' });
                                    }}
                                >
                                    Ping
                                </Button>
                                <Button
                                    size="small"
                                    fill="outline"
                                    color="success"
                                    onClick={() => {
                                        Toast.show({ content: 'DNS' });
                                    }}
                                >
                                    DNS
                                </Button>
                                <Button
                                    size="small"
                                    fill="outline"
                                    color="warning"
                                    onClick={() => {
                                        Toast.show({ content: '' });
                                    }}
                                >

                                </Button>
                            </div>
                        </Collapse.Panel>

                        <Collapse.Panel key="2" title="">
                            <div className="space-y-3">
                                <div className="flex items-center justify-between">
                                    <span className="text-sm">CPU</span>
                                    <div className="w-20 bg-gray-200 rounded-full h-1">
                                        <div className="bg-blue-500 h-1 rounded-full" style={{ width: '45%' }} />
                                    </div>
                                </div>
                                <div className="flex items-center justify-between">
                                    <span className="text-sm"></span>
                                    <div className="w-20 bg-gray-200 rounded-full h-1">
                                        <div className="bg-green-500 h-1 rounded-full" style={{ width: '62%' }} />
                                    </div>
                                </div>
                                <div className="flex items-center justify-between">
                                    <span className="text-sm"></span>
                                    <div className="w-20 bg-gray-200 rounded-full h-1">
                                        <div className="bg-yellow-500 h-1 rounded-full" style={{ width: '78%' }} />
                                    </div>
                                </div>
                            </div>
                        </Collapse.Panel>
                    </Collapse>
                </div>
            </Card>
        </div>
    );

    const renderCacheManagement = () => (
        <div className="space-y-4">
            {/*  */}
            <Card>
                <div className="p-4">
                    <div className="flex items-center justify-between mb-4">
                        <span className="text-lg font-semibold"></span>
                        <Space>
                            <Button
                                size="small"
                                color="danger"
                                onClick={clearCache}
                            >
                                <DeleteOutline className="mr-1" />

                            </Button>
                            <Button
                                size="small"
                                color="default"
                                onClick={updateCacheStats}
                            >
                                <RedoOutline className="mr-1" />

                            </Button>
                        </Space>
                    </div>

                    <div className="space-y-4">
                        <div className="flex items-center justify-between p-4 bg-gradient-to-r from-blue-50 to-blue-100 rounded-lg">
                            <div>
                                <div className="font-medium text-blue-800"></div>
                                <div className="text-sm text-blue-600">{cacheStats.size} </div>
                            </div>
                            <Badge content={cacheStats.size} color="blue" />
                        </div>

                        {cacheStats.keys.length > 0 && (
                            <div className="p-4 bg-gray-50 rounded-lg">
                                <div className="text-sm font-medium mb-2"></div>
                                <div className="text-xs text-gray-600 space-y-1">
                                    {cacheStats.keys.slice(0, 5).map((key, index) => (
                                        <div key={index} className="flex items-center">
                                            <StarOutline className="text-yellow-500 mr-1" />
                                            {key}
                                        </div>
                                    ))}
                                    {cacheStats.keys.length > 5 && (
                                        <div className="text-gray-500">...  {cacheStats.keys.length - 5} </div>
                                    )}
                                </div>
                            </div>
                        )}

                        {/*  */}
                        <div className="space-y-2">
                            <div className="flex items-center justify-between text-sm">
                                <span></span>
                                <span className="font-medium">{Math.round((cacheStats.size / 50) * 100)}%</span>
                            </div>
                            <div className="w-full bg-gray-200 rounded-full h-2">
                                <div
                                    className={`h-2 rounded-full transition-all duration-300 ${cacheStats.size > 40 ? 'bg-red-500' : 'bg-blue-500'
                                        }`}
                                    style={{ width: `${Math.round((cacheStats.size / 50) * 100)}%` }}
                                />
                            </div>
                        </div>
                    </div>
                </div>
            </Card>

            {/*  */}
            <Card>
                <div className="p-4">
                    <div className="mb-4">
                        <span className="text-lg font-semibold"></span>
                    </div>

                    <List>
                        <List.Item
                            prefix={<FileOutline />}
                            extra={<Tag color="blue">API</Tag>}
                        >
                            <div>
                                <div className="font-medium">API</div>
                                <div className="text-xs text-gray-500">API</div>
                            </div>
                        </List.Item>

                        <List.Item
                            prefix={<FileOutline />}
                            extra={<Tag color="green"></Tag>}
                        >
                            <div>
                                <div className="font-medium"></div>
                                <div className="text-xs text-gray-500">JSCSS</div>
                            </div>
                        </List.Item>

                        <List.Item
                            prefix={<StarOutline />}
                            extra={<Tag color="orange"></Tag>}
                        >
                            <div>
                                <div className="font-medium"></div>
                                <div className="text-xs text-gray-500"></div>
                            </div>
                        </List.Item>
                    </List>
                </div>
            </Card>
        </div>
    );

    const renderTestHistory = () => (
        <div className="space-y-4">
            {/*  */}
            <Card>
                <div className="p-4">
                    <div className="flex items-center justify-between mb-4">
                        <span className="text-lg font-semibold"></span>
                        <Space>
                            <Button
                                size="small"
                                color="primary"
                                onClick={generateReport}
                            >
                                <FileOutline className="mr-1" />

                            </Button>
                            <Button
                                size="small"
                                color="danger"
                                onClick={clearTestHistory}
                            >
                                <DeleteOutline className="mr-1" />

                            </Button>
                        </Space>
                    </div>

                    {testHistory.length === 0 ? (
                        <Empty
                            description=""
                            image={<FileOutline style={{ fontSize: 48, color: '#ccc' }} />}
                        />
                    ) : (
                        <div className="space-y-2">
                            {/*  */}
                            <div className="grid grid-cols-3 gap-2 mb-4">
                                <div className="text-center p-2 bg-blue-50 rounded">
                                    <div className="text-lg font-bold text-blue-600">{testHistory.length}</div>
                                    <div className="text-xs text-blue-600"></div>
                                </div>
                                <div className="text-center p-2 bg-green-50 rounded">
                                    <div className="text-lg font-bold text-green-600">
                                        {testHistory.filter(r => r.isOnline).length}
                                    </div>
                                    <div className="text-xs text-green-600"></div>
                                </div>
                                <div className="text-center p-2 bg-red-50 rounded">
                                    <div className="text-lg font-bold text-red-600">
                                        {testHistory.filter(r => !r.isOnline).length}
                                    </div>
                                    <div className="text-xs text-red-600"></div>
                                </div>
                            </div>

                            {/*  */}
                            <List>
                                {testHistory.slice(-10).reverse().map((result, index) => (
                                    <SwipeAction
                                        key={index}
                                        rightActions={[
                                            {
                                                key: 'delete',
                                                text: '',
                                                color: 'danger',
                                                onClick: () => {
                                                    Toast.show({ content: '' });
                                                }
                                            }
                                        ]}
                                    >
                                        <List.Item
                                            prefix={getStatusIcon(result.isOnline, result.networkQuality)}
                                            extra={
                                                <Tag color={getStatusColor(result.isOnline, result.networkQuality)}>
                                                    {getStatusText(result.isOnline, result.networkQuality)}
                                                </Tag>
                                            }
                                            arrow={<RightOutline />}
                                        >
                                            <div className="space-y-1">
                                                <div className="text-sm font-medium">
                                                    {new Date(result.timestamp).toLocaleString()}
                                                </div>
                                                <div className="text-xs text-gray-600 space-y-1">
                                                    <div>: {result.signalStrength}%</div>
                                                    <div>: {result.networkQuality}</div>
                                                    {result.pingTime && <div>Ping: {result.pingTime}ms</div>}
                                                </div>
                                            </div>
                                        </List.Item>
                                    </SwipeAction>
                                ))}
                            </List>
                        </div>
                    )}
                </div>
            </Card>
        </div>
    );

    return (
        <div className="min-h-screen bg-gray-50">
            <NavBar backArrow={false}>
                <div className="flex items-center">
                    <span className="text-lg font-semibold"></span>
                    <Badge content="v1.0" color="blue" style={{ marginLeft: 8 }} />
                </div>
            </NavBar>

            {/*  */}
            <NoticeBar
                content=""
                color="info"
                closeable
            />

            <div className="p-4 space-y-4">
                <Tabs
                    activeKey={activeTab}
                    onChange={setActiveTab}
                    style={{
                        '--title-font-size': '14px',
                        '--active-title-color': '#1677ff',
                        '--active-line-color': '#1677ff',
                    }}
                >
                    <Tabs.Tab title="" key="status">
                        {renderNetworkStatus()}
                    </Tabs.Tab>

                    <Tabs.Tab title="" key="controls">
                        {renderTestControls()}
                    </Tabs.Tab>

                    <Tabs.Tab title="" key="cache">
                        {renderCacheManagement()}
                    </Tabs.Tab>

                    <Tabs.Tab title="" key="history">
                        {renderTestHistory()}
                    </Tabs.Tab>
                </Tabs>
            </div>

            {/*  */}
            <Popup
                visible={showReport}
                onMaskClick={() => setShowReport(false)}
                position="bottom"
                bodyStyle={{ height: '70vh' }}
            >
                <div className="p-4">
                    <div className="flex items-center justify-between mb-4">
                        <span className="text-lg font-semibold"></span>
                        <Button
                            size="small"
                            onClick={() => setShowReport(false)}
                        >

                        </Button>
                    </div>

                    <div className="bg-gray-50 p-3 rounded-lg max-h-96 overflow-y-auto">
                        <pre className="text-xs whitespace-pre-wrap">
                            {networkTester.generateTestReport()}
                        </pre>
                    </div>
                </div>
            </Popup>
        </div>
    );
};

export default NetworkTest;

