import DeviceInfoPanel from '@/components/DeviceInfoPanel';
import deviceDetector from '@/utils/deviceDetector';
import {
    Button,
    Card,
    Dialog,
    Empty,
    Grid,
    List,
    NavBar,
    NoticeBar,
    Space,
    SpinLoading,
    Tabs,
    Tag,
    Toast
} from 'antd-mobile';
import {
    CheckCircleOutline,
    CloseCircleOutline,
    DeleteOutline,
    DeviceOutline,
    ExclamationCircleOutline,
    InfoCircleOutline,
    MinusCircleOutline,
    SetOutline,
    SpeedOutline,
    StarOutline
} from 'antd-mobile-icons';
import React, { useEffect, useState } from 'react';

const DeviceTest: React.FC = () => {
    const [activeTab, setActiveTab] = useState('device-info');
    const [deviceInfo, setDeviceInfo] = useState<any>(null);
    const [optimizationInfo, setOptimizationInfo] = useState<any>(null);
    const [testResults, setTestResults] = useState<any[]>([]);
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        // 
        const info = deviceDetector.getDeviceInfo();
        const optimization = deviceDetector.getOptimizationInfo();

        setDeviceInfo(info);
        setOptimizationInfo(optimization);

        // 
        deviceDetector.startMonitoring();
    }, []);

    const handleOptimizationChange = (optimizations: string[]) => {
        setOptimizationInfo(prev => ({ ...prev, optimizations }));
    };

    const runDeviceTest = async () => {
        setIsLoading(true);

        const results = [];

        // 
        await new Promise(resolve => setTimeout(resolve, 1000));

        // 
        results.push({
            id: 1,
            name: '',
            status: 'success',
            description: `: ${deviceInfo?.type}`,
            details: `: ${deviceInfo?.brand}, : ${deviceInfo?.model}`
        });

        // 
        results.push({
            id: 2,
            name: '',
            status: 'success',
            description: `: ${deviceInfo?.screenWidth}x${deviceInfo?.screenHeight}`,
            details: `: ${deviceInfo?.pixelRatio}x, : ${deviceInfo?.orientation}`
        });

        // 
        results.push({
            id: 3,
            name: '',
            status: deviceInfo?.isTouch ? 'success' : 'warning',
            description: deviceInfo?.isTouch ? '' : '',
            details: deviceInfo?.isTouch ? '' : ''
        });

        // 
        results.push({
            id: 4,
            name: '',
            status: deviceInfo?.isHighDPI ? 'success' : 'info',
            description: deviceInfo?.isHighDPI ? '' : '',
            details: deviceInfo?.isHighDPI ? 'DPI' : ''
        });

        // 
        results.push({
            id: 5,
            name: '',
            status: deviceInfo?.isLowMemory ? 'warning' : 'success',
            description: deviceInfo?.isLowMemory ? '' : '',
            details: deviceInfo?.isLowMemory ? '' : ''
        });

        // 
        results.push({
            id: 6,
            name: '',
            status: deviceInfo?.isSlowNetwork ? 'warning' : 'success',
            description: deviceInfo?.isSlowNetwork ? '' : '',
            details: deviceInfo?.isSlowNetwork ? '' : ''
        });

        setTestResults(results);
        setIsLoading(false);

        Toast.show({
            content: '',
            position: 'top'
        });
    };

    const clearTestResults = () => {
        Dialog.confirm({
            content: '',
            onConfirm: () => {
                setTestResults([]);
                Toast.show({
                    content: '',
                    position: 'top'
                });
            }
        });
    };

    const getStatusIcon = (status: string) => {
        switch (status) {
            case 'success':
                return <CheckCircleOutline className="text-green-500" />;
            case 'warning':
                return <ExclamationCircleOutline className="text-yellow-500" />;
            case 'error':
                return <CloseCircleOutline className="text-red-500" />;
            case 'info':
                return <InfoCircleOutline className="text-blue-500" />;
            default:
                return <MinusCircleOutline className="text-gray-500" />;
        }
    };

    const getStatusColor = (status: string) => {
        switch (status) {
            case 'success':
                return 'success';
            case 'warning':
                return 'warning';
            case 'error':
                return 'danger';
            case 'info':
                return 'primary';
            default:
                return 'default';
        }
    };

    const renderDeviceInfo = () => (
        <div className="space-y-4">
            <DeviceInfoPanel
                showDetails={true}
                onOptimizationChange={handleOptimizationChange}
            />

            <Card>
                <Card.Header title="" />
                <Card.Body>
                    <div className="grid grid-cols-2 gap-4">
                        <div className="text-center p-4 bg-green-50 rounded-lg">
                            <div className="text-2xl font-bold text-green-600">
                                {optimizationInfo?.optimizations.length || 0}
                            </div>
                            <div className="text-sm text-green-600"></div>
                        </div>
                        <div className="text-center p-4 bg-blue-50 rounded-lg">
                            <div className="text-2xl font-bold text-blue-600">
                                {optimizationInfo?.features.length || 0}
                            </div>
                            <div className="text-sm text-blue-600"></div>
                        </div>
                    </div>
                </Card.Body>
            </Card>
        </div>
    );

    const renderAdaptationTest = () => (
        <div className="space-y-4">
            <Card>
                <Card.Header
                    title=""
                    extra={
                        <Space>
                            <Button
                                size="small"
                                fill="outline"
                                onClick={runDeviceTest}
                                disabled={isLoading}
                            >
                                {isLoading ? <SpinLoading size="small" /> : <SpeedOutline />}

                            </Button>
                            <Button
                                size="small"
                                fill="outline"
                                onClick={clearTestResults}
                                disabled={testResults.length === 0}
                            >
                                <DeleteOutline />

                            </Button>
                        </Space>
                    }
                />
                <Card.Body>
                    <NoticeBar
                        content=""
                        color="info"
                    />
                </Card.Body>
            </Card>

            {testResults.length > 0 ? (
                <Card>
                    <Card.Header title="" />
                    <Card.Body>
                        <List>
                            {testResults.map((result) => (
                                <List.Item
                                    key={result.id}
                                    prefix={getStatusIcon(result.status)}
                                    title={
                                        <div className="flex items-center justify-between">
                                            <span>{result.name}</span>
                                            <Tag color={getStatusColor(result.status)} fill="outline">
                                                {result.status}
                                            </Tag>
                                        </div>
                                    }
                                    description={result.description}
                                    extra={
                                        <Button
                                            size="mini"
                                            fill="outline"
                                            onClick={() => {
                                                Toast.show({
                                                    content: result.details,
                                                    position: 'top'
                                                });
                                            }}
                                        >

                                        </Button>
                                    }
                                />
                            ))}
                        </List>
