import { Button, Card, List, NavBar, Space, Toast } from 'antd-mobile'
import React, { useEffect, useState } from 'react'
import { nativeBridge } from '@/utils/nativeBridge'
import { safeBack } from '@/utils/nav'

const BridgeTest: React.FC = () => {
  const [deviceInfo, setDeviceInfo] = useState<any>(null)
  const [networkInfo, setNetworkInfo] = useState<any>(null)
  const [clipboardText, setClipboardText] = useState<string>('')

  useEffect(() => {
    nativeBridge.getDeviceInfo().then(setDeviceInfo).catch(() => {})
    nativeBridge.getNetworkStatus().then(setNetworkInfo).catch(() => {})

    const offDeep = nativeBridge.addEventListener('deepLink', (data) => {
      Toast.show({ content: ` deepLink: ${data?.url || ''}` })
    })
    const offImg = nativeBridge.addEventListener('imagePicked', (data) => {
      Toast.show({ content: `imagePicked: ${data?.items?.length || 0} ` })
    })
    const offFiles = nativeBridge.addEventListener('filesPicked', (data) => {
      Toast.show({ content: `filesPicked: ${data?.items?.length || 0} ` })
    })
    return () => { offDeep(); offImg(); offFiles() }
  }, [])

  const rows: Array<{ key: string; action: () => void | Promise<void> }> = [
    {
      key: '',
      action: async () => {
        await nativeBridge.copyToClipboard('Hello from H5')
        Toast.show({ content: ': Hello from H5' })
      },
    },
    {
      key: '',
      action: async () => {
        const text = await nativeBridge.getClipboardText()
        setClipboardText(text || '')
        Toast.show({ content: `: ${text || '()'}` })
      },
    },
    {
      key: '',
      action: async () => {
        await nativeBridge.share({ title: '', text: ' Hybrid H5 ', url: location.href })
      },
    },
    {
      key: '',
      action: async () => {
        const files = await nativeBridge.openFilePicker('image/*', true)
        Toast.show({ content: ` ${files.length} ` })
      },
    },
    {
      key: '',
      action: async () => {
        try {
          const img = await nativeBridge.openCamera()
          Toast.show({ content: `: ${String(img).slice(0, 24)}...` })
        } catch (e) {
          Toast.show({ content: '' })
        }
      },
    },
    {
      key: '',
      action: async () => {
        await nativeBridge.vibrate(80)
        Toast.show({ content: '' })
      },
    },
    {
      key: '',
      action: async () => {
        await nativeBridge.setNavBar({ title: 'Hybrid Demo', showBack: true })
        Toast.show({ content: '' })
      },
    },
    {
      key: ' myapp://settings',
      action: async () => {
        await nativeBridge.openDeepLink('myapp://settings')
      },
    },
    {
      key: ' WebView',
      action: async () => {
        await nativeBridge.closeWebView()
      },
    },
    {
      key: '',
      action: async () => {
        const info = await nativeBridge.getDeviceInfo()
        setDeviceInfo(info)
        Toast.show({ content: '' })
      },
    },
    {
      key: '',
      action: async () => {
        const info = await nativeBridge.getNetworkStatus()
        setNetworkInfo(info)
        Toast.show({ content: '' })
      },
    },
  ]

  return (
    <div>
      <NavBar onBack={() => safeBack()}></NavBar>

      <div className="p-4 space-y-4">
        <Card>
          <Card.Header title="" />
          <Card.Body>
            <div className="text-sm text-gray-600">: {nativeBridge.getPlatform()}</div>
            <div className="text-sm text-gray-600">: {String(nativeBridge.isNativeEnvironment())}</div>
          </Card.Body>
        </Card>

        <Card>
          <Card.Header title="" />
          <Card.Body>
            <List>
              {rows.map((r) => (
                <List.Item key={r.key} onClick={() => r.action()}>
                  {r.key}
                </List.Item>
              ))}
            </List>
          </Card.Body>
        </Card>

        <Card>
          <Card.Header title="" />
          <Card.Body>
            <Space direction="vertical" block>
              <div className="text-sm">: {clipboardText || '()'}</div>
              <div className="text-sm">: {deviceInfo ? JSON.stringify(deviceInfo) : ''}</div>
              <div className="text-sm">: {networkInfo ? JSON.stringify(networkInfo) : ''}</div>
            </Space>
          </Card.Body>
        </Card>
      </div>
    </div>
  )
}

export default BridgeTest

