import { loading } from '@/stores'
import { Avatar, Badge, Empty, InfiniteScroll, List, PullToRefresh, SearchBar, Tabs } from 'antd-mobile'
import { BellOutline, MessageOutline } from 'antd-mobile-icons'
import React, { useEffect, useState } from 'react'
import { history } from 'umi'
import './Message.css'

interface MessageItem {
  id: string
  type: 'chat' | 'system' | 'notification'
  title: string
  content: string
  avatar?: string
  time: string
  unreadCount?: number
  isRead: boolean
}

const Message: React.FC = () => {
  const [searchValue, setSearchValue] = useState('')
  const [activeTab, setActiveTab] = useState('all')
  const [messages, setMessages] = useState<MessageItem[]>([])
  const [hasMore, setHasMore] = useState(true)
  // 

  // 
  const mockMessages: MessageItem[] = [
    {
      id: '1',
      type: 'chat',
      title: '',
      content: '',
      avatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=100&h=100&fit=crop&crop=face',
      time: '',
      unreadCount: 1,
      isRead: false,
    },
    {
      id: '2',
      type: 'system',
      title: '',
      content: '',
      time: '2',
      isRead: true,
    },
    {
      id: '3',
      type: 'notification',
      title: '',
      content: '',
      time: '1',
      unreadCount: 2,
      isRead: false,
    },
    {
      id: '4',
      type: 'chat',
      title: '',
      content: '3',
      avatar: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=100&h=100&fit=crop&crop=face',
      time: '10:30',
      isRead: true,
    },
    {
      id: '5',
      type: 'system',
      title: '',
      content: '3',
      time: '',
      unreadCount: 3,
      isRead: false,
    },
  ]

  useEffect(() => {
    loadMessages()
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  const loadMessages = async () => {
    try {
      console.log('Loading :', loading)
      console.log('Loading.show :', typeof loading.show)

      loading.show('...')
      console.log('Loading.show ')

      // API
      await new Promise(resolve => setTimeout(resolve, 1000))
      setMessages(mockMessages)
      console.log('')
    } catch (error) {
      console.error('Load messages failed:', error)
    } finally {
      loading.hide()
      console.log('Loading.hide ')
    }
  }

  const loadMore = async () => {
    try {
      // 
      await new Promise(resolve => setTimeout(resolve, 1000))
      setHasMore(false)
    } catch (error) {
      console.error('Load more failed:', error)
    }
  }

  const handleRefresh = async () => {
    await loadMessages()
  }

  const handleMessageClick = (message: MessageItem) => {
    if (message.type === 'chat') {
      history.push(`/message/${message.id}`)
    } else {
      // 
      console.log('Handle message:', message)
    }
  }

  const filteredMessages = messages.filter((message) => {
    const matchesSearch = message.title.toLowerCase().includes(searchValue.toLowerCase()) ||
      message.content.toLowerCase().includes(searchValue.toLowerCase())

    if (activeTab === 'all') return matchesSearch
    if (activeTab === 'unread') return matchesSearch && !message.isRead
    if (activeTab === 'chat') return matchesSearch && message.type === 'chat'
    if (activeTab === 'system') return matchesSearch && (message.type === 'system' || message.type === 'notification')

    return matchesSearch
  })

  const getMessageIcon = (type: MessageItem['type']) => {
    switch (type) {
      case 'chat':
        return <MessageOutline />
      case 'system':
      case 'notification':
        return <BellOutline />
      default:
        return <MessageOutline />
    }
  }

  const tabs = [
    { key: 'all', title: '' },
    { key: 'unread', title: '' },
    { key: 'chat', title: '' },
    { key: 'system', title: '' },
  ]

  const renderMessage = (message: MessageItem) => (
    <List.Item
      key={message.id}
      prefix={
        message.avatar ? (
          <Avatar src={message.avatar} style={{ '--size': '48px' }} />
        ) : (
          <div className="message-icon">
            {getMessageIcon(message.type)}
          </div>
        )
      }
      extra={
        <div className="message-extra">
          <div className="message-time">{message.time}</div>
          {message.unreadCount && (
            <Badge content={message.unreadCount} style={{ '--right': '-2px', '--top': '2px' }} />
          )}
        </div>
      }
      onClick={() => handleMessageClick(message)}
      className={`message-item ${!message.isRead ? 'unread' : ''}`}
    >
      <div className="message-content">
        <div className="message-title">{message.title}</div>
        <div className="message-text">{message.content}</div>
      </div>
    </List.Item>
  )

  return (
    <div className="message-page">
      <div className="message-header">
        <SearchBar
          placeholder=""
          value={searchValue}
          onChange={setSearchValue}
          style={{ '--border-radius': '20px' }}
        />
      </div>

      <Tabs activeKey={activeTab} onChange={setActiveTab}>
        {tabs.map(tab => (
          <Tabs.Tab title={tab.title} key={tab.key} />
        ))}
      </Tabs>

      <div className="message-list">
        <PullToRefresh onRefresh={handleRefresh}>
          {filteredMessages.length > 0 ? (
            <List>
              {filteredMessages.map(renderMessage)}
            </List>
          ) : (
            <Empty
              description=""
              image="https://gw.alipayobjects.com/zos/antfincdn/ZHrcdLPrvN/empty.svg"
            />
          )}
          <InfiniteScroll loadMore={loadMore} hasMore={hasMore}>
            {hasMore ? '...' : ''}
          </InfiniteScroll>
        </PullToRefresh>
      </div>
    </div>
  )
}

export default Message

