import { Button, Card, List, Toast } from 'antd-mobile'
import { CalendarOutline, ClockOutline, LocationOutline } from 'antd-mobile-icons'
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'umi'

interface CheckinRecord {
    id: string
    date: string
    time: string
    type: 'in' | 'out'
    location: string
    status: 'success' | 'late' | 'early'
}

const Checkin: React.FC = () => {
    const [loading, setLoading] = useState(false)
    const [todayRecords, setTodayRecords] = useState<CheckinRecord[]>([])
    const [currentTime, setCurrentTime] = useState(new Date())
    const [location, setLocation] = useState('...')
    const navigate = useNavigate()

    useEffect(() => {
        // 
        const timer = setInterval(() => {
            setCurrentTime(new Date())
        }, 1000)

        // 
        getLocation()

        // 
        loadTodayRecords()

        return () => clearInterval(timer)
    }, [])

    const getLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords
                    // API
                    setLocation('xxx')
                },
                (error) => {
                    console.error(':', error)
                    setLocation('')
                }
            )
        } else {
            setLocation('')
        }
    }

    const loadTodayRecords = () => {
        // 
        const mockRecords: CheckinRecord[] = [
            {
                id: '1',
                date: '2025-01-27',
                time: '09:00',
                type: 'in',
                location: 'xxx',
                status: 'success'
            },
            {
                id: '2',
                date: '2025-01-27',
                time: '12:00',
                type: 'out',
                location: 'xxx',
                status: 'success'
            }
        ]
        setTodayRecords(mockRecords)
    }

    const handleCheckin = async (type: 'in' | 'out') => {
        setLoading(true)

        try {
            // 
            await new Promise(resolve => setTimeout(resolve, 1000))

            const newRecord: CheckinRecord = {
                id: Date.now().toString(),
                date: currentTime.toISOString().split('T')[0],
                time: currentTime.toLocaleTimeString('zh-CN', {
                    hour: '2-digit',
                    minute: '2-digit'
                }),
                type,
                location,
                status: 'success'
            }

            setTodayRecords(prev => [...prev, newRecord])

            Toast.show({
                content: `${type === 'in' ? '' : ''}`,
                position: 'center',
            })
        } catch (error) {
            Toast.show({
                content: '',
                position: 'center',
            })
        } finally {
            setLoading(false)
        }
    }

    const getStatusText = (status: string) => {
        switch (status) {
            case 'success':
                return ''
            case 'late':
                return ''
            case 'early':
                return ''
            default:
                return ''
        }
    }

    const getStatusColor = (status: string) => {
        switch (status) {
            case 'success':
                return 'text-green-600'
            case 'late':
                return 'text-red-600'
            case 'early':
                return 'text-orange-600'
            default:
                return 'text-green-600'
        }
    }

    const formatTime = (date: Date) => {
        return date.toLocaleTimeString('zh-CN', {
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit'
        })
    }

    const formatDate = (date: Date) => {
        return date.toLocaleDateString('zh-CN', {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
            weekday: 'long'
        })
    }

    return (
        <div className="p-4 bg-gray-50 min-h-screen">
            {/*  */}
            <Card className="mb-4 text-center">
                <div className="text-3xl font-bold text-gray-900 mb-2">
                    {formatTime(currentTime)}
                </div>
                <div className="text-sm text-gray-500">
                    {formatDate(currentTime)}
                </div>
            </Card>

            {/*  */}
            <Card className="mb-4">
                <div className="flex items-center">
                    <LocationOutline className="mr-2 text-blue-500" />
                    <span className="text-sm text-gray-600">{location}</span>
                </div>
            </Card>

            {/*  */}
            <div className="grid grid-cols-2 gap-4 mb-6">
                <Button
                    block
                    color="primary"
                    size="large"
                    loading={loading}
                    onClick={() => handleCheckin('in')}
                    className="h-12 text-lg font-medium"
                >

                </Button>
                <Button
                    block
                    color="success"
                    size="large"
                    loading={loading}
                    onClick={() => handleCheckin('out')}
                    className="h-12 text-lg font-medium"
                >

                </Button>
            </div>

            {/*  */}
            <Card>
                <div className="flex items-center mb-4">
                    <CalendarOutline className="mr-2 text-blue-500" />
                    <span className="font-medium"></span>
                </div>

                {todayRecords.length > 0 ? (
                    <List>
                        {todayRecords.map((record) => (
                            <List.Item
                                key={record.id}
                                prefix={
                                    <div className={`w-3 h-3 rounded-full ${record.type === 'in' ? 'bg-blue-500' : 'bg-green-500'
                                        }`} />
                                }
                                extra={
                                    <span className={`text-sm ${getStatusColor(record.status)}`}>
                                        {getStatusText(record.status)}
                                    </span>
                                }
                            >
                                <div className="flex items-center">
                                    <ClockOutline className="mr-2 text-gray-400" />
                                    <span className="font-medium">
                                        {record.type === 'in' ? '' : ''}
                                    </span>
                                    <span className="ml-2 text-gray-500">{record.time}</span>
                                </div>
                                <div className="text-xs text-gray-400 mt-1">
                                    {record.location}
                                </div>
                            </List.Item>
                        ))}
                    </List>
                ) : (
                    <div className="text-center py-8 text-gray-500">

                    </div>
                )}
            </Card>
        </div>
    )
}

export default Checkin

