import {
    ActionSheet,
    Button,
    Checkbox,
    Empty,
    NavBar,
    Toast
} from 'antd-mobile'
import {
    DownOutline,
    LeftOutline
} from 'antd-mobile-icons'
import React, { useCallback, useEffect, useState } from 'react'
import { history, useLocation } from 'umi'
import './index.css'

interface FileItem {
    id: string
    name: string
    type: 'word' | 'excel' | 'pdf' | 'image' | 'other'
    size: string
    status: 'pending' | 'electronic' | 'archive' | 'manual' | 'marked'
    modifyTime: string
    serialNumber: string
    legalId: string
    documentNumber: string
    printCount: number
}

interface TaskFileData {
    fileType: 'template' | 'attachment'
    files: FileItem[]
    taskId?: string
    taskTitle?: string
}

interface FileListProps {
    fileType?: 'template' | 'attachment'
    files?: FileItem[]
}

const FileList: React.FC<FileListProps> = ({
    fileType: propFileType,
    files: propFiles
}) => {
    const location = useLocation()
    const [fileType, setFileType] = useState<'template' | 'attachment'>('template')
    const [files, setFiles] = useState<FileItem[]>([])
    const [taskInfo, setTaskInfo] = useState<{ taskId?: string, taskTitle?: string }>({})
    const [selectedFiles, setSelectedFiles] = useState<string[]>([])
    const [expandedFiles, setExpandedFiles] = useState<string[]>([])
    const [showActions, setShowActions] = useState(false)

    // 
    useEffect(() => {
        console.log(' FileList ', { location, propFileType, propFiles })

        // 
        const routeState = location.state as TaskFileData | undefined
        if (routeState) {
            console.log(' :', routeState)
            setFileType(routeState.fileType)
            setFiles(routeState.files || [])
            setTaskInfo({
                taskId: routeState.taskId,
                taskTitle: routeState.taskTitle
            })
        } else {
            // URLprops
            const urlParams = new URLSearchParams(location.search)
            const urlFileType = urlParams.get('type') as 'template' | 'attachment'

            if (urlFileType) {
                setFileType(urlFileType)
                console.log(' URL:', urlFileType)
            } else if (propFileType) {
                setFileType(propFileType)
                console.log(' props:', propFileType)
            }

            if (propFiles) {
                setFiles(propFiles)
                console.log(' props:', propFiles)
            }
        }
    }, [location, propFileType, propFiles])

    const handleSelectAll = useCallback(() => {
        if (selectedFiles.length === files.length) {
            setSelectedFiles([])
        } else {
            setSelectedFiles(files.map(f => f.id))
        }
    }, [selectedFiles, files])

    const handleSelectFile = useCallback((fileId: string) => {
        setSelectedFiles(prev =>
            prev.includes(fileId)
                ? prev.filter(id => id !== fileId)
                : [...prev, fileId]
        )
    }, [])

    const toggleFileExpand = useCallback((fileId: string) => {
        setExpandedFiles(prev =>
            prev.includes(fileId)
                ? prev.filter(id => id !== fileId)
                : [...prev, fileId]
        )
    }, [])

    const handleOperation = () => {
        if (selectedFiles.length === 0) {
            Toast.show('')
            return
        }
        setShowActions(true)
    }

    const handleView = () => {
        Toast.show(` ${selectedFiles.length} `)
        setShowActions(false)
    }

    const handleDownload = () => {
        Toast.show(` ${selectedFiles.length} `)
        setShowActions(false)
    }

    const getFileIcon = (type: FileItem['type']) => {
        const iconMap = {
            word: '',
            excel: '',
            pdf: '',
            image: '',
            other: ''
        }
        return iconMap[type] || ''
    }

    const getStatusText = (status: FileItem['status']) => {
        const statusMap = {
            pending: '',
            electronic: '',
            archive: '',
            manual: '',
            marked: ''
        }
        return statusMap[status] || status
    }

    const renderFileItem = (file: FileItem) => {
        const isSelected = selectedFiles.includes(file.id)
        const isExpanded = expandedFiles.includes(file.id)

        return (
            <div key={file.id} className="file-item">
                <div className={`file-header ${isExpanded ? 'expanded' : ''}`}>
                    <Checkbox
                        className="file-checkbox"
                        checked={isSelected}
                        onChange={() => handleSelectFile(file.id)}
                    />
                    <div className={`file-icon ${file.type}`}>
                        {getFileIcon(file.type)}
                    </div>
                    <div className="file-info">
                        <div className="file-name">{file.name}</div>
                        <div className="file-meta">
                            <span className="file-type">{file.type === 'other' ? '' : file.type}</span>
                            <span className="file-size">{file.size}MB</span>
                        </div>
                    </div>
                    <Button
                        className="expand-button"
                        onClick={() => toggleFileExpand(file.id)}
                    >
                        <DownOutline className={`expand-icon ${isExpanded ? 'expanded' : ''}`} />
                    </Button>
                </div>
                {isExpanded && (
                    <div className="file-details">
                        <div className="detail-grid">
                            <div className="detail-row">
                                <span className="detail-label"></span>
                                <span className="detail-value">{file.type === 'other' ? '' : file.type}</span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label"></span>
                                <span className="detail-value">{Math.floor(Math.random() * 20 + 1)}</span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label"></span>
                                <span className="detail-value"></span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label">ID</span>
                                <span className="detail-value"></span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label"></span>
                                <span className="detail-value highlight">{file.serialNumber}</span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label"></span>
                                <span className={`detail-value status-${file.status}`}>
                                    {getStatusText(file.status)}
                                </span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label"></span>
                                <span className="detail-value">{file.modifyTime}</span>
                            </div>
                            <div className="detail-row">
                                <span className="detail-label"></span>
                                <span className="detail-value">{Math.floor(Math.random() * 20 + 1)}</span>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        )
    }

    return (
        <div className="file-list-page">
            <NavBar
                className="file-list-nav"
                onBack={() => {
                    console.log(' ')
                    try {
                        history.back()
                    } catch (error) {
                        console.error(':', error)
                        if (taskInfo.taskId) {
                            history.push(`/task-process/detail/${taskInfo.taskId}`)
                        } else {
                            history.push('/task-process')
                        }
                    }
                }}
                backIcon={<LeftOutline />}
            >
                {fileType === 'template' ? '' : ''}
            </NavBar>

            <div className="file-list-content">
                {files.length === 0 ? (
                    <Empty
                        className="empty-file-list"
                        description=""
                        imageStyle={{ width: 64, height: 64 }}
                    />
                ) : (
                    files.map(renderFileItem)
                )}
            </div>

            <div className="file-list-footer">
                <div className="footer-left">
                    <Button
                        className="select-all-btn"
                        onClick={handleSelectAll}
                    >
                        {selectedFiles.length === files.length ? '' : ''}
                    </Button>
                    <span className="selected-count">
                         <span className="count">{selectedFiles.length}</span> 
                    </span>
                </div>
                <Button
                    className="operation-btn"
                    color="primary"
                    disabled={selectedFiles.length === 0}
                    onClick={handleOperation}
                >

                </Button>
            </div>

            {/*  - ui12.jpg */}
            <ActionSheet
                visible={showActions}
                actions={[
                    {
                        text: '',
                        key: 'view',
                        style: { color: '#333333' }
                    },
                    {
                        text: '',
                        key: 'download',
                        style: { color: '#333333' }
                    }
                ]}
                cancelText=""
                onClose={() => setShowActions(false)}
                onAction={(action) => {
                    if (action.key === 'view') {
                        handleView()
                    } else if (action.key === 'download') {
                        handleDownload()
                    }
                }}
            />
        </div>
    )
}

export default FileList

