/**
 *  API 
 * 
 * @umijs/max
 */

import type {
    Organization,
    Pagination,
    ProcessRecord,
    ProcessStep,
    TaskDetail,
    TaskFilter,
    TaskItem
} from '@/stores'
import { TaskPriority, TaskStatus } from '@/stores/taskProcess/types'

// API 
const BASE_URL = '/api/task-process'

// 
interface ApiResponse<T = any> {
    success: boolean
    code: number
    message: string
    data: T
    timestamp?: number
}

// 
interface ListResponse<T> {
    list: T[]
    pagination: Pagination
}

/**
 *  API
 */
export const taskListApi = {
    // 
    async getTaskList(params?: {
        filter?: TaskFilter
        page?: number
        pageSize?: number
    }): Promise<ListResponse<TaskItem>> {
        // API
        await new Promise(resolve => setTimeout(resolve, 300))

        // 
        const mockTasks = getMockTaskList()
        const page = params?.page || 1
        const pageSize = params?.pageSize || 10
        const start = (page - 1) * pageSize
        const end = start + pageSize

        return {
            list: mockTasks.slice(start, end),
            pagination: {
                current: page,
                pageSize,
                total: mockTasks.length,
                totalPages: Math.ceil(mockTasks.length / pageSize)
            }
        }
    },

    // 
    async getTaskStatistics(): Promise<{
        total: number
        pending: number
        processing: number
        completed: number
        rejected: number
    }> {
        // API
        await new Promise(resolve => setTimeout(resolve, 200))

        return {
            total: 128,
            pending: 45,
            processing: 32,
            completed: 38,
            rejected: 13
        }
    }
}

/**
 *  API
 */
export const taskDetailApi = {
    // 
    async getTaskDetail(taskId: string): Promise<TaskDetail> {
        // API
        await new Promise(resolve => setTimeout(resolve, 300))
        // 
        return getMockTaskDetail(taskId)
    }
}

/**
 *  API
 */
export const processApi = {
    // 
    async getProcessRecords(taskId: string): Promise<ProcessRecord[]> {
        // API
        await new Promise(resolve => setTimeout(resolve, 200))
        return getMockProcessRecords(taskId)
    },

    // 
    async submitProcess(data: {
        taskId: string
        action: string
        opinion?: string
        nextStep?: string
        nextOrg?: string
        notifyUsers?: string[]
        attachments?: string[]
    }): Promise<boolean> {
        // API
        await new Promise(resolve => setTimeout(resolve, 500))
        console.log(':', data)
        return true
    },

    // 
    async saveDraft(data: {
        taskId: string
        nextStep?: string
        nextOrg?: string
        notifyUsers?: string[]
        opinion?: string
        urgentLevel?: string
        isUrgent?: boolean
        attachments?: string[]
    }): Promise<boolean> {
        // API
        await new Promise(resolve => setTimeout(resolve, 300))
        console.log(':', data)
        return true
    }
}

/**
 *  API
 */
export const organizationApi = {
    // 
    async getOrganizations(): Promise<Organization[]> {
        // API
        await new Promise(resolve => setTimeout(resolve, 200))
        return getMockOrganizations()
    },

    // 
    async getNextStepsByOrg(orgId: string): Promise<ProcessStep[]> {
        // API
        await new Promise(resolve => setTimeout(resolve, 200))
        return getMockProcessSteps()
    },

    // 
    async getNextOrgsByStep(stepId: string): Promise<Organization[]> {
        // API
        await new Promise(resolve => setTimeout(resolve, 200))
        return getMockOrganizations()
    },

    // 
    async getNotifyUsers(orgId: string): Promise<any[]> {
        // API
        await new Promise(resolve => setTimeout(resolve, 200))
        return getMockUsers()
    }
}

/**
 *  API
 */
export const fileApi = {
    // 
    async upload(file: File): Promise<{ url: string; name: string }> {
        // API
        await new Promise(resolve => setTimeout(resolve, 1000))
        return {
            url: `https://example.com/files/${file.name}`,
            name: file.name
        }
    },

    // 
    async download(url: string, filename: string): Promise<void> {
        // API
        await new Promise(resolve => setTimeout(resolve, 500))
        console.log(':', url, filename)
    }
}

// ====================  ====================

// 
function getMockTaskList(): TaskItem[] {
    return Array.from({ length: 10 }, (_, index) => ({
        id: `task_${Date.now()}_${index}`,
        title: `${index + 1} - `,
