/**
 * API
 * HTTP
 */

import { useAuthStore } from '@/stores/auth/useAuthStore'
import { toast } from '@/stores/ui/useUIStore'
import axios, { AxiosError, AxiosRequestConfig, AxiosResponse } from 'axios'
import { appConfig } from '../config/env'

// API
export interface ApiResponse<T = any> {
    code: number
    message: string
    data: T
    success: boolean
    timestamp: number
}

// API
export interface ApiError {
    code: number
    message: string
    details?: any
}

// axios
export const apiClient = axios.create({
    baseURL: appConfig.apiBaseUrl,
    timeout: appConfig.apiTimeout,
    headers: {
        'Content-Type': 'application/json',
    },
})

// 
apiClient.interceptors.request.use(
    (config) => {
        // token
        const { token } = useAuthStore.getState()
        if (token) {
            config.headers.Authorization = `Bearer ${token}`
        }

        // ID
        config.headers['X-Request-ID'] = `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`

        // 
        if (appConfig.isDev) {
            console.log(` API Request: ${config.method?.toUpperCase()} ${config.url}`, {
                headers: config.headers,
                params: config.params,
                data: config.data,
            })
        }

        return config
    },
    (error) => {
        console.error(' Request Error:', error)
        return Promise.reject(error)
    }
)

// 
apiClient.interceptors.response.use(
    (response: AxiosResponse<ApiResponse>) => {
        const { data } = response

        // 
        if (appConfig.isDev) {
            console.log(` API Response: ${response.config.method?.toUpperCase()} ${response.config.url}`, {
                status: response.status,
                data,
            })
        }

        // 
        if (!data.success && data.code !== 200) {
            const error: ApiError = {
                code: data.code,
                message: data.message,
                details: data.data,
            }

            // 
            toast.error(data.message)

            return Promise.reject(error)
        }

        return response
    },
    async (error: AxiosError<ApiResponse>) => {
        const { response, config } = error

        // 
        if (appConfig.isDev) {
            console.error(` API Error: ${config?.method?.toUpperCase()} ${config?.url}`, {
                status: response?.status,
                data: response?.data,
                message: error.message,
            })
        }

        // HTTP
        if (response) {
            const { status, data } = response

            switch (status) {
                case 401:
                    // 
                    useAuthStore.getState().logout()
                    toast.error('')
                    // 
                    break

                case 403:
                    toast.error('')
                    break

                case 404:
                    toast.error('')
                    break

                case 422:
                    // 
                    if (data?.data && typeof data.data === 'object') {
                        const firstErrorMessage = Object.values(data.data)[0]
                        toast.error(firstErrorMessage as string)
                    } else {
                        toast.error(data?.message || '')
                    }
                    break

                case 429:
                    toast.error('')
                    break

                case 500:
                    toast.error('')
                    break

                default:
                    toast.error(data?.message || ` (${status})`)
            }

            // 
            const apiError: ApiError = {
                code: status,
                message: data?.message || error.message,
                details: data?.data,
            }

            return Promise.reject(apiError)
        }

        // 
        if (error.code === 'ECONNABORTED') {
            toast.error('')
        } else if (!navigator.onLine) {
            toast.error('')
        } else {
            toast.error('')
        }

        return Promise.reject({
            code: 0,
            message: error.message,
            details: error,
        } as ApiError)
    }
)

// 
export const api = {
    get: <T = any>(url: string, config?: AxiosRequestConfig) =>
        apiClient.get<ApiResponse<T>>(url, config).then(res => res.data.data),

    post: <T = any>(url: string, data?: any, config?: AxiosRequestConfig) =>
        apiClient.post<ApiResponse<T>>(url, data, config).then(res => res.data.data),

    put: <T = any>(url: string, data?: any, config?: AxiosRequestConfig) =>
        apiClient.put<ApiResponse<T>>(url, data, config).then(res => res.data.data),

    patch: <T = any>(url: string, data?: any, config?: AxiosRequestConfig) =>
        apiClient.patch<ApiResponse<T>>(url, data, config).then(res => res.data.data),

    delete: <T = any>(url: string, config?: AxiosRequestConfig) =>
        apiClient.delete<ApiResponse<T>>(url, config).then(res => res.data.data),
}

// 
export const uploadFile = async (
    url: string,
    file: File,
    onProgress?: (percent: number) => void
) => {
    const formData = new FormData()
    formData.append('file', file)

    return apiClient.post<ApiResponse<{ url: string; filename: string }>>(url, formData, {
        headers: {
            'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
            if (progressEvent.total && onProgress) {
                const percent = Math.round((progressEvent.loaded * 100) / progressEvent.total)
                onProgress(percent)
            }
        },
    }).then(res => res.data.data)
}

// 
export const concurrent = <T extends readonly unknown[] | []>(
    requests: readonly [...{ [K in keyof T]: Promise<T[K]> }]
): Promise<T> => {
    return Promise.all(requests) as Promise<T>
}

// 
export const retry = async <T>(
    request: () => Promise<T>,
    maxRetries: number = 3,
    delay: number = 1000
): Promise<T> => {
    let lastError: any

    for (let i = 0; i < maxRetries; i++) {
        try {
            return await request()
        } catch (error) {
            lastError = error
            if (i < maxRetries - 1) {
                await new Promise(resolve => setTimeout(resolve, delay * Math.pow(2, i)))
            }
        }
    }

    throw lastError
}

export default api
